// --- MOVIMENTAÇÃO EM 8 DIREÇÕES (TOP-DOWN) ---
// Permite que o personagem se mova livremente nas 8 direções usando um joystick.
// Ideal para jogos com visão de cima ou com gravidade zero.

let joystick;

// Nomes das animações (devem corresponder aos nomes que você criou no editor)
const WALK_ANIM = "andando";
const IDLE_ANIM = "parado";

// Velocidade de movimento
const MOVE_SPEED = 250;

// Guarda a escala X original para o flip
let originalScaleX;

function start() {
    // Encontre o objeto joystick na cena
    joystick = find("joystick");
    if (!joystick) {
        console.error("Objeto com tag 'joystick' não encontrado! A movimentação não funcionará.");
    }

    // Salva a escala X inicial para saber a direção original do sprite
    originalScaleX = this.scale.x;

    // Inicia com a animação de parado, se existir
    this.animation.play(IDLE_ANIM);
}

function update(deltaTime) {
    if (!this.physics || !joystick) return;

    let isMoving = false;

    // --- Movimento com Joystick ---
    if (joystick.joystick.isTouching) {
        const inputX = joystick.joystick.x;
        const inputY = joystick.joystick.y;

        // Calcula a magnitude para verificar se o joystick está sendo movido
        const magnitude = Math.sqrt(inputX * inputX + inputY * inputY);

        if (magnitude > 0.1) {
            isMoving = true;

            // Normaliza o vetor de direção para garantir velocidade constante em todas as direções
            const dirX = inputX / magnitude;
            const dirY = inputY / magnitude;

            // Define a velocidade do corpo físico
            this.physics.setVelocity(dirX * MOVE_SPEED, dirY * MOVE_SPEED);

            // --- Flip (Espelhamento) do Sprite ---
            if (inputX > 0.1) {
                // Movendo para a direita
                this.scale = { x: Math.abs(originalScaleX), y: this.scale.y };
            } else if (inputX < -0.1) {
                // Movendo para a esquerda
                this.scale = { x: -Math.abs(originalScaleX), y: this.scale.y };
            }
        } else {
             // Para o movimento se o joystick estiver no centro
             this.physics.setVelocity(0, 0);
        }
    } else {
        // Para o movimento se o joystick não estiver sendo tocado
        this.physics.setVelocity(0, 0);
    }

    // --- Lógica de Animação ---
    if (isMoving) {
        // Se estiver se movendo, toca a animação de andar
        this.animation.play(WALK_ANIM);
    } else {
        // Se estiver parado, toca a animação de parado
        this.animation.play(IDLE_ANIM);
    }
}
